import React, { Component } from 'react';

const Head = {
  width: "100%",
  //marginLeft: "240px",
  backgroundColor: "#2F89FC",
  minHeight: "50px",
  textAlign: "center",
  padding: "8px",
  display: "flex",
  justifyContent:"flex-end",
  alignItems:"center"
}

{/*const Side = {
  height: "400px",
  width: "240px",
  top: "0px",
  backgroundColor: "rgba(230, 230, 220)",
  textAlign: "center",
}*/}
const oneinput ={
  border:"1px solid RGBA(0,0,0,.3)",
  borderRadius: "5px",
  padding: "2px",
 }

const Card = {
    background: "white",
    height: "400px",
    width: "300px",
    position: "fixed",
    top: "10vh",
    left:"2vw",
    display: "block",
    margin: "0 auto",
    fontSize: "20px",
    fontWeight: "700",
    textAlign: "center",
    padding: "40px",
    borderRadius:"5px",
    boxShadow:"0 2px 2px 0 rgba(0,0,0,0.14), 0 3px 1px -2px rgba(0,0,0,0.12), 0 1px 5px 0 rgba(0,0,0,0.2)"
}

const Card2 = {
    background: "white",
    height: "400px",
    width: "700px",
    position: "fixed",
    top: "10vh",
    left:"40vw",
    marginLeft: "200vw",
    display: "block",
    margin: "0 auto",
    fontSize: "20px",
    fontWeight: "700",
    textAlign: "center",
    padding: "40px",
    borderRadius:"5px",
    boxShadow:"0 2px 2px 0 rgba(0,0,0,0.14), 0 3px 1px -2px rgba(0,0,0,0.12), 0 1px 5px 0 rgba(0,0,0,0.2)"
}

const Container = {
  width: "100%",
  height: "calc( 100vh - 50px)",
}

var today = new Date();
var dd = today.getDate();
var mm = today.getMonth() + 1; //January is 0!
var yyyy = today.getFullYear();

if (dd < 10) {
  dd = '0' + dd;
}

if (mm < 10) {
  mm = '0' + mm;
}

today = dd + '/' + mm + '/' + yyyy;

var imageName = require('./avatar1.jpg')

class App extends Component {

  constructor() {
          super();
          this.state = {
            placeholder:'Nombre'
          };
        }

  render() {
    return (

      <div className="App">

        <div style={Head}>

                <form className="text-right search" method="GET">
                    <input style={oneinput} name="q" placeholder="Buscar" type="text"/>
                </form>
        </div>

        <div style={Container}>
          <div style={Card}>
            <img src={imageName} alt="avatar1" width="100" height="100" />
            <br /><br />
            <label>Usuario</label>
          <h1>{today}</h1>
          </div>
          <div style={Card2}>
            <img src={imageName} alt="avatar1" width="100" height="100" />
            <br /><br />
            <label>Usuario</label>
          <h1>{today}</h1>
          </div>

        </div>


                        {/*<div class="row">
                            <div class="col-sm-8 col-sm-offset-2 data-post">
                                <p>Lorem Ipsum Dolor si amet</p>
                                <div class="reaction">
                                    <img draggable="false" class="emoji" alt="❤" src="https://twemoji.maxcdn.com/16x16/2764.png"/> 156
                                </div>
                                <div class="comments">
                                    <div class="more-comments">Mas comentarios</div>
                                    <ul>
                                        <li><b>User1</b> Lorem Ipsum Dolor si amet</li>
                                        <li><b>User2</b> Lorem Ipsum Dolor si amet <img draggable="false" class="emoji" alt="😂" src="https://twemoji.maxcdn.com/16x16/1f602.png"/></li>
                                    </ul>
                                    <form>
                                        <input class="form-control" placeholder="Agregar un comentario" type="text"/>
                                    </form>
                                </div>
                            </div>
                        </div>*/}


      </div>

    );

  }
}

export default App;
